function useInView(ref: React.RefObject<HTMLElement>) {
  const [isInView, setIsInView] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
        ([entry]) => {
          setIsInView(entry.isIntersecting);
        },
        {threshold: 0.2}
    );

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => {
      observer.disconnect();
    };
  }, [ref]);

  return isInView;
}

function AnimatedNumber({end, duration = 2000}: { end: number; duration?: number }) {
  const numberRef = useRef<HTMLSpanElement>(null);
  const isInView = useInView(numberRef);
  const [hasStarted, setHasStarted] = useState(false);
  const [count, setCount] = useState(0);

  useEffect(() => {
    if (isInView && !hasStarted) {
      setHasStarted(true);
    }
  }, [isInView]);

  useEffect(() => {
    if (!hasStarted) return;

    let startTime: number | null = null;
    let animationFrame: number;

    const animate = (currentTime: number) => {
      if (!startTime) startTime = currentTime;
      const progress = (currentTime - startTime) / duration;

      if (progress < 1) {
        setCount(Math.min(Math.floor(end * progress), end));
        animationFrame = requestAnimationFrame(animate);
      } else {
        setCount(end);
      }
    };

    animationFrame = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationFrame);
  }, [end, duration, hasStarted]);

  return <span ref={numberRef}>{count}</span>;
}

function TypewriterText({lines}: { lines: string[] }) {
  const containerRef = useRef<HTMLParagraphElement>(null);
  const isInView = useInView(containerRef);
  const [hasStarted, setHasStarted] = useState(false);
  const [displayedLines, setDisplayedLines] = useState<string[]>([]);
  const [currentLine, setCurrentLine] = useState(0);
  const [currentChar, setCurrentChar] = useState(0);
  const speed = 15;

  useEffect(() => {
    if (isInView && !hasStarted) {
      setHasStarted(true);
    }
  }, [isInView]);

  useEffect(() => {
    if (!hasStarted) return;
    if (currentLine >= lines.length) return;

    const interval = setInterval(() => {
      if (currentChar < lines[currentLine].length) {
        setDisplayedLines(prev => {
          const newLines = [...prev];
          if (!newLines[currentLine]) newLines[currentLine] = '';
          newLines[currentLine] = lines[currentLine].slice(0, currentChar + 1);
          return newLines;
        });
        setCurrentChar(prev => prev + 1);
      } else {
        clearInterval(interval);
        if (currentLine < lines.length - 1) {
          setTimeout(() => {
            setCurrentLine(prev => prev + 1);
            setCurrentChar(0);
          }, 100);
        }
      }
    }, speed);

    return () => clearInterval(interval);
  }, [currentLine, currentChar, lines, hasStarted]);

  return (
      <p ref={containerRef} className="text-[#FFDE00] text-[32px] font-['Unbounded'] ml-[-40px] mt-8 h-[160px]">
        {displayedLines.map((line, index) => (
            <React.Fragment key={index}>
              {line}
              {index < displayedLines.length - 1 && <br/>}
            </React.Fragment>
        ))}
      </p>
  );
}

